(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    gpii.requests.request.handler.userUpdate = function (requestProxy, token) {
        // Update the profile here
        /* Currently the update takes place in presonalControlPanel.pcpUpdate */
        
        requestProxy.events.onSuccess.fire("User with token " + token + " was successfully updated.\n");
    };

})();
